const issue = $json.body.issue;
const userMap = {
  'pavel': 'Pavel Novák',
  'tomas': 'Tomáš Novotný'
  // Add other user mappings here
};

// Find the user tag (e.g., #pavel, #tomas)
const userTag = issue.tags.find(tag => Object.keys(userMap).includes(tag.name));

// Check for required tags
const hasRMTag = issue.tags.some(tag => tag.name === 'RM');

if (!hasRMTag || !userTag) {
  console.log('Skipping issue: Missing #RM tag or a valid user tag.');
  return []; // Stop the workflow
}

// Find deadline custom field
const deadlineField = issue.customFields.find(cf => cf.projectCustomField.field.name === 'Deadline');

const taskData = {
  youtrackId: issue.idReadable,
  title: issue.summary,
  assignee: userMap[userTag.name],
  deadline: deadlineField ? deadlineField.value : null
};

$json.taskData = taskData;

return $json;
